SELECT left(s.DATE_SERV,7)
	,s.HOSPCODE
	,d.DIDSTD
	,s.COST
	,s.PRICE

FROM chospital INNER JOIN service as s ON chospital.hoscode = s.HOSPCODE
	INNER JOIN drug_opd as d 
	ON chospital.hoscode = d.HOSPCODE AND d.HOSPCODE = s.HOSPCODE
	AND d.PID = s.PID AND d.SEQ = s.SEQ AND d.DATE_SERV = s.DATE_SERV
	
WHERE LEFT(s.DATE_SERV, 7) BETWEEN '2017-10' and '2018-09'
	AND d.DIDSTD IN ('410000000109150020111376','410000000479150020111376','410000000449150020111376','410000000319150020111376','410000000230000020111376','420000000110000021511376','410000000230000020111376','420000000110000021511376','410000000510000020111376','410000000199150020111376','410000000490000020111376','410000000570000020111376','410000000619200334111376','410000000649200334111376','410000000159200334111376','410000000579200334111376','410000000199200334111376','410000000269403141911376','410000000389403141911376','410000000389500530411376',
'410000000389601940111376','410000000380000040611376','420000001930000040611376','420000003960000020311376','420000004489220044211376','420000002930000094711376','410000000680000099900000',
'420000005430000099900000','420000001540000094711376','410000000060000094511376','420000005300000020311376','420000004910000020311376','300000000000700750411376')
	
GROUP BY left(s.DATE_SERV,7)
ORDER BY s.DATE_SERV